@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {

  credentials = {
    email: '',
    password: ''
  };

  constructor() { }

  name: string | undefined;
  email: string | undefined;

  onSubmit() {
    console.log('Ad: ' + this.name);
    console.log('Email: ' + this.email);
