import { Component } from '@angular/core';
import { LoginService } from '../auth/login.service';
import { LoginViewModel } from '../models/LoginViewModel';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  model: LoginViewModel = new LoginViewModel();
  errorMessage: string = "";

  constructor(private loginService: LoginService, private router: Router) { }

  login(): void {
    this.loginService.login(this.model)
      .subscribe(
        response => {
          console.log(response);
          this.router.navigate(['/form']);
        },
        error => {
          console.log(error);
          this.errorMessage = error.error.message;
        }
      );
  }
}
<div class="alert alert-danger" *ngIf="errorMessage">
  {{ errorMessage }}
</div>

<form (submit)="login()" #loginForm="ngForm">
  <div class="form-group">
    <label for="username">Kullanıcı Adı</label>
    <input type="text" class="form-control" id="username" name="username"
      [(ngModel)]="model.username" required>
    <div class="invalid-feedback">Kullanıcı adı boş bırakılamaz</div>
  </div>

  <div class="form-group">
    <label for="password">Şifre</label>
    <input type="password" class="form-control" id="password" name="password"
      [(ngModel)]="model.password" required>
    <div class="invalid-feedback">Şifre boş bırakılamaz</div>
  </div>

  <button type="submit" class="btn btn-primary" [disabled]="!loginForm.valid">Giriş Yap</button>
</form>
