import { Component } from '@angular/core';
import { AuthService } from '../../app/auth/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {

  credentials = {
    email: '',
    password: ''
  };

  constructor(private authService: AuthService) { }

  login() {
    this.authService.login(this.credentials).subscribe(
      response => {
        // Başarılı giriş işlemi
        console.log('Giriş işlemi başarılı: ', response);
      },
      error => {
        // Hatalı giriş işlemi
        console.log('Giriş işlemi hatalı: ', error);
      }
    );
  }
}
<h2>Giriş Yap</h2>
<form>
  <label for="email">Email</label>
</form>
<form (ngSubmit)="login()">
  <div class="form-group">
    <label for="email">Email</label>
    <input type="email" class="form-control" id="email" [(ngModel)]="credentials.email" name="email" required>
  </div>
  <div class="form-group">
    <label for="password">Şifre</label>
    <input type="password" class="form-control" id="password" [(ngModel)]="credentials.password" name="password" required>
  </div>
  <button type="submit" class="btn btn-primary">Giriş Yap</button>
</form>
