import { Component } from '@angular/core';
import { RegisterViewModel } from '../models/RegisterViewModel';
import { RegisterService } from '../auth/register.service';
import { catchError, throwError } from 'rxjs';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})

export class RegisterComponent {
  model: RegisterViewModel = new RegisterViewModel();
  errorMessage: "string" | undefined;

  constructor(private registerService: RegisterService) { }

  register(): void {
    this.registerService.register(this.model)
      .pipe(
        catchError((error) => {
          this.errorMessage = error.error.message;
          return throwError('Error');
        })
      )
      .subscribe(
        response => console.log(response),
        error => console.log(error)
      );
  }
}
<div class="alert alert-danger" *ngIf="errorMessage">
  {{ errorMessage }}
</div>

<form (submit)="register()" #registerForm="ngForm">
  <div class="form-group">
    <label for="firstName">Adı</label>
    <input type="text" class="form-control" id="firstName" name="firstName"
      [(ngModel)]="model.firstName" required>
    <div class="invalid-feedback">Adı boş bırakılamaz</div>
  </div>

  <div class="form-group">
    <label for="lastName">Soyadı</label>
    <input type="text" class="form-control" id="lastName" name="lastName"
      [(ngModel)]="model.lastName" required>
    <div class="invalid-feedback">Soyadı boş bırakılamaz</div>
  </div>

  <div class="form-group">
    <label for="email">E-posta</label>
    <input type="email" class="form-control" id="email" name="email"
      [(ngModel)]="model.email" required>
    <div class="invalid-feedback">E-posta boş bırakılamaz ve geçerli olmalıdır</div>
  </div>

  <div class="form-group">
    <label for="password">Şifre</label>
    <input type="password" class="form-control" id="password" name="password"
      [(ngModel)]="model.password" required>
    <div class="invalid-feedback">Şifre boş bırakılamaz</div>
  </div>

  <button type="submit" class="btn btn-primary" [disabled]="!registerForm.valid">Kayıt Ol</button>
</form>
